###########################################################################/**# @RdocFunction toBase64## @title "Encodes the content of an file in Base64 format"## \description{#  @get "title".# }## @synopsis## \arguments{#   \item{pathname}{The file to be encoded.}#   \item{prefix}{If @TRUE, the return Base64 string will be prepended#    with character sequence '\code{data:<mime type>;base64,}'.}#   \item{...}{Not used.}# }## \value{#   Returns a @character string.# }## @author## \seealso{#   Internally the \pkg{markdown} package is utilized.# }## @keyword device# @keyword utilities# @keyword internal#*/###########################################################################toBase64 <- function(pathname, prefix=TRUE, ...) {  # Argument 'pathname':  pathname <- Arguments$getReadablePathname(pathname);  # Locate 'markdown' function  ns <- getNamespace("markdown");  .b64EncodeFile <- get(".b64EncodeFile", mode="function", envir=ns);  # Encode  res <- .b64EncodeFile(pathname);  # Drop prefix?  if (!prefix) {    res <- gsub("^data:[^;]*;base64,", "", res);  }  res;} # toBase64()############################################################################### HISTORY:# 2013-08-27# o Created.##############################################################################